import{_ as e,c as a,o as d,a as t}from"./app.8eb1196d.js";const f=JSON.parse('{"title":"updated、beforeUpdate钩子的一些理解","description":"","frontmatter":{},"headers":[{"level":2,"title":"钩子基础","slug":"钩子基础","link":"#钩子基础","children":[]},{"level":2,"title":"在updated里改变数据会引发该回调的循环吗？","slug":"在updated里改变数据会引发该回调的循环吗","link":"#在updated里改变数据会引发该回调的循环吗","children":[]}],"relativePath":"面试/NONI/Vue/updated、beforeUpdate钩子的一些理解.md"}'),p={name:"面试/NONI/Vue/updated、beforeUpdate钩子的一些理解.md"},r=t('<h1 id="updated、beforeupdate钩子的一些理解" tabindex="-1">updated、beforeUpdate钩子的一些理解 <a class="header-anchor" href="#updated、beforeupdate钩子的一些理解" aria-hidden="true">#</a></h1><h2 id="钩子基础" tabindex="-1">钩子基础 <a class="header-anchor" href="#钩子基础" aria-hidden="true">#</a></h2><p>beforeUpdate触发：在页面更新渲染完成后，DOM树更新前被调用</p><p>updated触发：在页面DOM树更新后被调用。（父组件的更新钩子将在其子组件的更新钩子之后调用）</p><blockquote><p>需要注意的是如果只是改变了DOM中的数据（data），未对页面造成任何影响，就不会触发beforeUpdate,updated方法。</p></blockquote><h2 id="在updated里改变数据会引发该回调的循环吗" tabindex="-1">在<strong>updated</strong>里改变数据会引发该回调的循环吗？ <a class="header-anchor" href="#在updated里改变数据会引发该回调的循环吗" aria-hidden="true">#</a></h2><p>若在updated中，字段赋的值为固定值，第一次赋值会触发updated、beforeUpdate；</p><p>如果是自增这种，程序就会陷入死循环。</p>',8),o=[r];function _(n,u,s,i,c,h){return d(),a("div",null,o)}const b=e(p,[["render",_]]);export{f as __pageData,b as default};
